<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use Illuminate\Support\Facades\DB;use Illuminate\Support\Facades\Input;use Carbon\Carbon;use App\Common;use Illuminate\Validation\Rule;use Auth;use Validator;class AuthController extends Controller{    public function login(Request $request) {        $remember = (Input::has('remember')) ? true : false;        $email = $request->input('email');        $password = $request->input('password');        $data=array ( 'email' => $email,'password' => $password);        if (Auth::attempt($data,$remember)) {            return response()->json(                ["msg"=>"login successfully","status"=>true],200);        } else {            return response()->json(                ["msg"=>"Your email or password is invalid!","status"=>false],400);        }    }}